import os
import glob
import heapq
import tqdm

# 参数配置
SEED_DIR = "./seedresult/"
TEMP_DIR = "./temp_chunks/"
OUTPUT_FILE = "merged_seedresult.txt"
MISSING_FILE = "missing_seeds.txt"
COMPRESSED_FILE = "compressed_missing.txt"
CHUNK_SIZE = 500_000
THRESHOLD = 10000

os.makedirs(TEMP_DIR, exist_ok=True)


def parse_line(line):
    try:
        parts = line.strip().split()
        if len(parts) == 4:
            maxcount, x, z, seed = map(int, parts)
            return (seed, x, z, maxcount)  # 正确位置标记
    except:
        return None


def write_chunk(chunk, index):
    chunk.sort()
    with open(f"{TEMP_DIR}/chunk_{index}.txt", "w", encoding="utf-8") as f:
        for seed, x, z, maxcount in chunk:
            f.write(f"{seed} {x} {z} {maxcount}\n")  # 输出格式修正


def process_all_files():
    chunk = []
    chunk_idx = 0
    total_lines = 0
    file_list = glob.glob(os.path.join(SEED_DIR, "*.txt"))

    for file in tqdm.tqdm(file_list, desc="读取文件", unit="file"):
        with open(file, "r", encoding="utf-8") as f:
            for line in f:
                parsed = parse_line(line)
                if parsed:
                    chunk.append(parsed)
                    total_lines += 1
                if len(chunk) >= CHUNK_SIZE:
                    write_chunk(chunk, chunk_idx)
                    chunk.clear()
                    chunk_idx += 1

    if chunk:
        write_chunk(chunk, chunk_idx)

    print(f"\n共处理行数: {total_lines}，生成分块数: {chunk_idx + 1}")
    external_merge(chunk_idx + 1)


def external_merge(total_chunks):
    files = []
    min_heap = []
    seen = set()

    for i in range(total_chunks):
        f = open(f"{TEMP_DIR}/chunk_{i}.txt", "r", encoding="utf-8")
        line = f.readline()
        if line:
            parsed = parse_line(line)
            if parsed:
                seed = parsed[0]
                heapq.heappush(min_heap, (seed, parsed, i))
        files.append(f)

    with open(OUTPUT_FILE, "w", encoding="utf-8") as out:
        while min_heap:
            _, entry, file_idx = heapq.heappop(min_heap)
            seed, x, z, maxcount = entry
            if seed not in seen:
                out.write(f"{seed} {x} {z} {maxcount}\n")
                seen.add(seed)

            line = files[file_idx].readline()
            if line:
                parsed = parse_line(line)
                if parsed:
                    next_seed = parsed[0]
                    heapq.heappush(min_heap, (next_seed, parsed, file_idx))

    for f in files:
        f.close()

    print(f"\n合并完成，输出文件：{OUTPUT_FILE}")
    detect_missing_seeds()


def detect_missing_seeds():
    seeds = []
    with open(OUTPUT_FILE, "r", encoding="utf-8") as f:
        for line in f:
            parts = line.strip().split()
            if len(parts) == 4:
                try:
                    seeds.append(int(parts[0]))
                except:
                    continue

    seeds.sort()

    with open(MISSING_FILE, "w", encoding="utf-8") as missing_f, \
            open(COMPRESSED_FILE, "w", encoding="utf-8") as compressed_f:

        for i in tqdm.tqdm(range(len(seeds) - 1), desc="标记缺失种子"):
            a = seeds[i]
            b = seeds[i + 1]
            gap = b - a
            if gap <= 1:
                continue
            elif gap > THRESHOLD:
                compressed_f.write(f"{a + 1}~{b - 1}\n")
            else:
                for missing_seed in range(a + 1, b):
                    missing_f.write(f"{missing_seed}\n")

    print(f"\n缺失分析完成：\n- {MISSING_FILE}\n- {COMPRESSED_FILE}")


if __name__ == "__main__":
    process_all_files()

